"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[8956],{1877:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Introducci\xf3n a SleakOps","href":"/es/","docId":"index","unlisted":false},{"type":"link","label":"Conceptos B\xe1sicos","href":"/es/basicconcepts","docId":"basicconcepts","unlisted":false},{"type":"link","label":"Empezando","href":"/es/gettingstarted","docId":"gettingstarted","unlisted":false},{"type":"category","label":"Provider","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Cuentas","href":"/es/provider/accounts","docId":"provider/accounts","unlisted":false},{"type":"link","label":"Eliminar un Provider","href":"/es/provider/deleting-a-provider","docId":"provider/deleting-a-provider","unlisted":false},{"type":"link","label":"Errores Comunes","href":"/es/provider/common-errors","docId":"provider/common-errors","unlisted":false},{"type":"link","label":"Dise\xf1ando tu Infraestructura","href":"/es/provider/schemas","docId":"provider/schemas","unlisted":false}],"href":"/es/provider/"},{"type":"category","label":"Cluster","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Accede a tu Cl\xfaster","href":"/es/cluster/access-cluster","docId":"cluster/access-cluster","unlisted":false},{"type":"category","label":"Addons","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"EBS (Elastic Block Store)","href":"/es/cluster/addons/ebs","docId":"cluster/addons/ebs","unlisted":false},{"type":"link","label":"EFS (Elastic File System)","href":"/es/cluster/addons/efs","docId":"cluster/addons/efs","unlisted":false},{"type":"link","label":"Grafana","href":"/es/cluster/addons/grafana","docId":"cluster/addons/grafana","unlisted":false},{"type":"link","label":"Kubecost","href":"/es/cluster/addons/kubecost","docId":"cluster/addons/kubecost","unlisted":false},{"type":"link","label":"Loki","href":"/es/cluster/addons/loki","docId":"cluster/addons/loki","unlisted":false},{"type":"link","label":"Open Telemetry","href":"/es/cluster/addons/otel","docId":"cluster/addons/otel","unlisted":false},{"type":"link","label":"Prometheus","href":"/es/cluster/addons/prometheus","docId":"cluster/addons/prometheus","unlisted":false}],"href":"/es/cluster/addons/"},{"type":"category","label":"Node Pools","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Creando un Node Pool","href":"/es/cluster/nodepools/creating-nodepool","docId":"cluster/nodepools/creating-nodepool","unlisted":false},{"type":"link","label":"Gestionando un Node Pool","href":"/es/cluster/nodepools/managing-nodepool","docId":"cluster/nodepools/managing-nodepool","unlisted":false}],"href":"/es/cluster/nodepools/"},{"type":"link","label":"Apagado del Cluster","href":"/es/cluster/shutdown-cluster","docId":"cluster/shutdown-cluster","unlisted":false}],"href":"/es/cluster/"},{"type":"category","label":"Entorno","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Delegaci\xf3n de dominio","href":"/es/environment/delegate_domains","docId":"environment/delegate_domains","unlisted":false}],"href":"/es/environment/"},{"type":"category","label":"Project","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Configura tu Dockerfile","href":"/es/project/configure_your_dockerfile","docId":"project/configure_your_dockerfile","unlisted":false},{"type":"category","label":"Dependency","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"AWS Memcached","href":"/es/project/dependency/memcached-aws","docId":"project/dependency/memcached-aws","unlisted":false},{"type":"link","label":"MySQL en AWS","href":"/es/project/dependency/mysql-aws","docId":"project/dependency/mysql-aws","unlisted":false},{"type":"link","label":"AWS OpenSearch","href":"/es/project/dependency/opensearch-aws","docId":"project/dependency/opensearch-aws","unlisted":false},{"type":"link","label":"AWS Oracle","href":"/es/project/dependency/oracle-aws","docId":"project/dependency/oracle-aws","unlisted":false},{"type":"link","label":"AWS PosgreSQL","href":"/es/project/dependency/postgresql-aws","docId":"project/dependency/postgresql-aws","unlisted":false},{"type":"link","label":"AWS Redis","href":"/es/project/dependency/redis-aws","docId":"project/dependency/redis-aws","unlisted":false},{"type":"link","label":"AWS S3 Bucket","href":"/es/project/dependency/s3bucket-aws","docId":"project/dependency/s3bucket-aws","unlisted":false},{"type":"link","label":"AWS SQS","href":"/es/project/dependency/sqs-aws","docId":"project/dependency/sqs-aws","unlisted":false}],"href":"/es/project/dependency/"},{"type":"category","label":"Workloads","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Web Service","href":"/es/project/workload/webservice","docId":"project/workload/webservice","unlisted":false},{"type":"link","label":"Worker","href":"/es/project/workload/worker","docId":"project/workload/worker","unlisted":false},{"type":"link","label":"Cronjobs","href":"/es/project/workload/cronjob","docId":"project/workload/cronjob","unlisted":false},{"type":"link","label":"Hooks","href":"/es/project/workload/hook","docId":"project/workload/hook","unlisted":false},{"type":"link","label":"Job","href":"/es/project/workload/job","docId":"project/workload/job","unlisted":false}],"href":"/es/project/workload/"},{"type":"link","label":"VarGroup","href":"/es/project/vargroup/","docId":"project/vargroup/index","unlisted":false},{"type":"category","label":"Deployment","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Un poco mas sobre los Deployments:","href":"/es/project/deployment/more_on_deployment","docId":"project/deployment/more_on_deployment","unlisted":false},{"type":"link","label":"Release","href":"/es/project/deployment/release","docId":"project/deployment/release","unlisted":false}],"href":"/es/project/deployment/"},{"type":"link","label":"Build","href":"/es/project/build/","docId":"project/build/index","unlisted":false},{"type":"link","label":"Volumes","href":"/es/project/volumes","docId":"project/volumes","unlisted":false}],"href":"/es/project/"},{"type":"category","label":"User","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Autenticaci\xf3n de la Consola de AWS","href":"/es/user/aws_console_authentication","docId":"user/aws_console_authentication","unlisted":false},{"type":"link","label":"Conexi\xf3n a la VPN","href":"/es/user/vpn","docId":"user/vpn","unlisted":false}],"href":"/es/user/"},{"type":"link","label":"CLI de Sleakops","href":"/es/cli","docId":"cli","unlisted":false},{"type":"link","label":"Conectar a Git","href":"/es/connect_to_git","docId":"connect_to_git","unlisted":false},{"type":"link","label":"Registro de Cambios","href":"/es/changelog","docId":"changelog","unlisted":false},{"type":"link","label":"Modelo de Responsabilidad Compartida","href":"/es/responsability-model","docId":"responsability-model","unlisted":false}]},"docs":{"basicconcepts":{"id":"basicconcepts","title":"Conceptos B\xe1sicos","description":"Proveedor","sidebar":"tutorialSidebar"},"changelog":{"id":"changelog","title":"Registro de Cambios","description":"Version 1.7.9","sidebar":"tutorialSidebar"},"cli":{"id":"cli","title":"CLI","description":"Optimiza tu CI/CD con la CLI de SleakOps","sidebar":"tutorialSidebar"},"cluster/access-cluster":{"id":"cluster/access-cluster","title":"Accede a tu Cl\xfaster","description":"Para acceder a tu cl\xfaster de Kubernetes, debes completar algunos pasos y generar un archivo Kubeconfig para conectarte a un IDE de Kubernetes.","sidebar":"tutorialSidebar"},"cluster/addons/ebs":{"id":"cluster/addons/ebs","title":"EBS (Elastic Block Store)","description":"En SleakOps, AWS EBS (Elastic Block Store) se utiliza para proporcionar almacenamiento en bloque persistente para instancias EC2. Los vol\xfamenes EBS son especialmente \xfatiles para aplicaciones que requieren acceso de baja latencia a datos y alto rendimiento.","sidebar":"tutorialSidebar"},"cluster/addons/efs":{"id":"cluster/addons/efs","title":"EFS (Elastic File System)","description":"En SleakOps, AWS EFS (Elastic File System) se utiliza principalmente para gestionar los vol\xfamenes de los Proyectos. EFS ofrece un sistema de archivos escalable y el\xe1stico que puede montarse en m\xfaltiples instancias EC2, permitiendo que los pods de un cl\xfaster de Kubernetes compartan el mismo sistema de archivos de manera eficiente.","sidebar":"tutorialSidebar"},"cluster/addons/grafana":{"id":"cluster/addons/grafana","title":"Grafana","description":"Introducci\xf3n","sidebar":"tutorialSidebar"},"cluster/addons/index":{"id":"cluster/addons/index","title":"Addons","description":"En SleakOps, los Add-ons mejoran la funcionalidad de tu cl\xfaster EKS al proporcionar componentes esenciales y opcionales que optimizan operaciones, mejoran el rendimiento y aumentan la visibilidad. Los Add-ons esenciales incluyen herramientas para monitoreo, gesti\xf3n de DNS, balanceo de carga y aprovisionamiento inteligente de nodos, asegurando que tu cl\xfaster sea robusto y eficiente.","sidebar":"tutorialSidebar"},"cluster/addons/kubecost":{"id":"cluster/addons/kubecost","title":"Kubecost: Monitoreo de Costos del Cl\xfaster","description":"Introducci\xf3n Kubecost  es una herramienta esencial de gesti\xf3n de costos integrada en SleakOps que proporciona visibilidad detallada de los costos de tu cl\xfaster de Kubernetes. Te ayuda a comprender los gastos de tu cl\xfaster desglos\xe1ndolos por namespaces, despliegues y workloads individuales.","sidebar":"tutorialSidebar"},"cluster/addons/loki":{"id":"cluster/addons/loki","title":"Loki","description":"Introducci\xf3n","sidebar":"tutorialSidebar"},"cluster/addons/otel":{"id":"cluster/addons/otel","title":"Open Telemetry","description":"In Sleakops Opentelemetry  is an addon that enables you to understand the internal state of a system by examining its telemetry data, which includes traces and metrics. Opentelemetry is installed alongside Prometheus  to store valuable metrics about the system and  Grafana  to visualize the data.","sidebar":"tutorialSidebar"},"cluster/addons/prometheus":{"id":"cluster/addons/prometheus","title":"Prometheus: Sistema de Monitoreo","description":"Prometheus  es un sistema de monitoreo utilizado para recopilar m\xe9tricas de todas las cargas de trabajo computacionales de Kubernetes. Est\xe1 enfocado en m\xe9tricas de utilizaci\xf3n de recursos y rendimiento de aplicaciones.","sidebar":"tutorialSidebar"},"cluster/index":{"id":"cluster/index","title":"Cl\xfaster","description":"En SleakOps, los cl\xfasteres de Kubernetes son esenciales para implementar y ejecutar tus aplicaciones de manera efectiva.","sidebar":"tutorialSidebar"},"cluster/nodepools/creating-nodepool":{"id":"cluster/nodepools/creating-nodepool","title":"Creando un Node Pool","description":"1. Accede a la configuraci\xf3n de tu cl\xfaster para acceder a la secci\xf3n de Node Pools","sidebar":"tutorialSidebar"},"cluster/nodepools/index":{"id":"cluster/nodepools/index","title":"Node Pools","description":"Un Node Pool es esencialmente una colecci\xf3n de nodos dentro de un cl\xfaster de Kubernetes que tienen configuraciones similares, como el mismo tipo de m\xe1quina, sistema operativo y tama\xf1o de instancia.","sidebar":"tutorialSidebar"},"cluster/nodepools/managing-nodepool":{"id":"cluster/nodepools/managing-nodepool","title":"Gestionando un Node Pool","description":"Aqu\xed encontrar\xe1s:","sidebar":"tutorialSidebar"},"cluster/shutdown-cluster":{"id":"cluster/shutdown-cluster","title":"Apagado del Cluster","description":"Es la capacidad de apagar todos los recursos de tu cluster en un horario programado para evitar incurrir en costos durante ese per\xedodo de tiempo.","sidebar":"tutorialSidebar"},"connect_to_git":{"id":"connect_to_git","title":"Conectar tu Cuenta Git","description":"Para aprovechar todo el potencial de SleakOps, necesitar\xe1s conectar tu cuenta Git a nuestra plataforma. Esta conexi\xf3n permite a SleakOps acceder a tus repositorios, habilitando una integraci\xf3n perfecta, despliegues automatizados y una gesti\xf3n eficiente de tus bases de c\xf3digo.","sidebar":"tutorialSidebar"},"environment/delegate_domains":{"id":"environment/delegate_domains","title":"Delegaci\xf3n de dominio","description":"Un aspecto cr\xedtico al trabajar con SleakOps es garantizar una delegaci\xf3n adecuada de dominios. La delegaci\xf3n de dominios es esencial para una integraci\xf3n y funcionalidad sin problemas, ya que determina c\xf3mo se gestionan tus dominios y subdominios y c\xf3mo interact\xfaan con tu entorno en AWS. Dependiendo de tus necesidades espec\xedficas, hay varios escenarios de delegaci\xf3n de dominios con SleakOps:","sidebar":"tutorialSidebar"},"environment/index":{"id":"environment/index","title":"Entorno","description":"Un entorno es un espacio aislado donde recursos, proyectos y dependencias espec\xedficos pueden operar de manera independiente. Este aislamiento mejora la organizaci\xf3n, la seguridad y la gesti\xf3n de recursos en sistemas o plataformas de mayor escala.","sidebar":"tutorialSidebar"},"gettingstarted":{"id":"gettingstarted","title":"Empezando","description":"Inicia sesi\xf3n con tu correo electr\xf3nico","sidebar":"tutorialSidebar"},"index":{"id":"index","title":"\xa1Bienvenido a SleakOps!","description":"Bienvenido a SleakOps, tu soluci\xf3n ideal para gestionar aplicaciones escalables en Kubernetes sin esfuerzo. SleakOps automatiza tareas tediosas, optimiza la administraci\xf3n de recursos en AWS y Kubernetes, y ofrece una interfaz web intuitiva para gestionar tu infraestructura de forma fluida.","sidebar":"tutorialSidebar"},"project/build/index":{"id":"project/build/index","title":"Build","description":"Hemos hablado repetidamente sobre el Build tanto en la Documentaci\xf3n de Proyectos como en la Documentaci\xf3n del Build Inicial. Un build es b\xe1sicamente una plantilla de un sistema operativo, bibliotecas y otras dependencias del proyecto que despliegas.","sidebar":"tutorialSidebar"},"project/configure_your_dockerfile":{"id":"project/configure_your_dockerfile","title":"Configura tu Dockerfile","description":"Configurar tu Dockerfile es crucial porque define c\xf3mo se construye y empaqueta tu aplicaci\xf3n en un contenedor. Un Dockerfile configurado correctamente garantiza que tu imagen Docker contenga todas las dependencias y configuraciones necesarias, permitiendo que tu aplicaci\xf3n se ejecute sin problemas en tu Environment.","sidebar":"tutorialSidebar"},"project/dependency/index":{"id":"project/dependency/index","title":"Dependencias: Integraci\xf3n de Bases de Datos, Cach\xe9 y Servicios de Mensajer\xeda","description":"Una dependencia en el contexto de SleakOps se refiere a cualquier infraestructura o servicio externo del cual tu aplicaci\xf3n depende para funcionar de manera efectiva. Estas dependencias incluyen una variedad de servicios y recursos de AWS que tu aplicaci\xf3n integra para mejorar sus capacidades y rendimiento.","sidebar":"tutorialSidebar"},"project/dependency/memcached-aws":{"id":"project/dependency/memcached-aws","title":"AWS Memcached","description":"SleakOps integra Amazon ElastiCache con Memcached, un servicio de cach\xe9 en memoria completamente gestionado. Esta integraci\xf3n permite cachear datos frecuentemente accedidos, mejorando el rendimiento de las aplicaciones al reducir la carga en tus bases de datos. Con SleakOps, gestionar y desplegar clusters de Memcached se vuelve sencillo, proporcionando escalabilidad sin interrupciones y un rendimiento optimizado para tus aplicaciones.","sidebar":"tutorialSidebar"},"project/dependency/mysql-aws":{"id":"project/dependency/mysql-aws","title":"MySQL en AWS","description":"SleakOps facilita la integraci\xf3n de bases de datos MySQL a trav\xe9s de Amazon RDS (Relational Database Service). Amazon RDS para MySQL es un servicio completamente administrado que simplifica la configuraci\xf3n, operaci\xf3n y escalado de bases de datos. Al usar SleakOps para esta integraci\xf3n, puedes gestionar eficientemente bases de datos MySQL dentro de tu entorno EKS (Elastic Kubernetes Service), asegurando soluciones de almacenamiento de datos robustas, escalables y confiables para tus aplicaciones.","sidebar":"tutorialSidebar"},"project/dependency/opensearch-aws":{"id":"project/dependency/opensearch-aws","title":"AWS OpenSearch","description":"SleakOps se integra con Amazon OpenSearch (anteriormente conocido como Amazon Elasticsearch Service), un potente motor de b\xfasqueda y an\xe1lisis dise\xf1ado para ayudarte a buscar, analizar y visualizar datos en tiempo real. Al utilizar OpenSearch en SleakOps, puedes implementar, gestionar y escalar r\xe1pidamente tu infraestructura de b\xfasqueda, obteniendo valiosos conocimientos de tus datos.","sidebar":"tutorialSidebar"},"project/dependency/oracle-aws":{"id":"project/dependency/oracle-aws","title":"AWS Oracle","description":"SleakOps facilitates the integration of Oracle databases through Amazon RDS (Relational Database Service). Amazon RDS for Oracle is a fully managed service that simplifies database setup, operation, and scaling. By leveraging SleakOps for this integration, you can efficiently manage Oracle databases within your EKS (Elastic Kubernetes Service) environment, ensuring robust, scalable, and reliable data storage solutions for your applications.","sidebar":"tutorialSidebar"},"project/dependency/postgresql-aws":{"id":"project/dependency/postgresql-aws","title":"AWS PosgreSQL","description":"SleakOps ofrece una integraci\xf3n fluida con Amazon RDS para PostgreSQL, un servicio de base de datos relacional completamente administrado. PostgreSQL es conocido por sus caracter\xedsticas avanzadas, escalabilidad y rendimiento robusto, lo que lo convierte en una opci\xf3n preferida para aplicaciones complejas y operaciones intensivas en datos. Al integrar PostgreSQL a trav\xe9s de SleakOps, puedes gestionar eficientemente tus implementaciones de bases de datos y aprovechar el poder del servicio administrado de AWS.","sidebar":"tutorialSidebar"},"project/dependency/redis-aws":{"id":"project/dependency/redis-aws","title":"AWS Redis","description":"SleakOps facilita la integraci\xf3n de Amazon ElastiCache con Redis, un sistema de almacenamiento de datos en memoria completamente gestionado. Este servicio te permite almacenar en cach\xe9 los datos a los que se accede con frecuencia, mejorando el rendimiento de la aplicaci\xf3n y reduciendo la carga en tus bases de datos principales. Al usar Redis a trav\xe9s de SleakOps, tus aplicaciones pueden experimentar tiempos de respuesta m\xe1s r\xe1pidos, mejor escalabilidad y menor latencia.","sidebar":"tutorialSidebar"},"project/dependency/s3bucket-aws":{"id":"project/dependency/s3bucket-aws","title":"AWS S3 Bucket","description":"SleakOps se integra perfectamente con Amazon Simple Storage Service (S3), un servicio de almacenamiento de objetos altamente duradero y seguro. Amazon S3 proporciona una soluci\xf3n escalable para almacenar y recuperar cualquier cantidad de datos, lo que lo convierte en una opci\xf3n ideal para almacenamiento de copias de seguridad, gesti\xf3n de activos y archivo de datos dentro de tus aplicaciones. Al aprovechar SleakOps para la integraci\xf3n con S3, puedes gestionar f\xe1cilmente tus cubos S3 y acceder a los datos almacenados directamente desde tu entorno EKS (Elastic Kubernetes Service).","sidebar":"tutorialSidebar"},"project/dependency/sqs-aws":{"id":"project/dependency/sqs-aws","title":"AWS SQS","description":"SleakOps integra Amazon Simple Queue Service (SQS) en la arquitectura de tu aplicaci\xf3n. Amazon SQS es un servicio de colas de mensajes totalmente gestionado que te permite desacoplar los componentes de tu aplicaci\xf3n, asegurando un procesamiento eficiente y fluido de los mensajes. Al aprovechar SQS, puedes mejorar la escalabilidad y confiabilidad de tus aplicaciones, manejando grandes vol\xfamenes de mensajes de manera asincr\xf3nica.","sidebar":"tutorialSidebar"},"project/deployment/index":{"id":"project/deployment/index","title":"Deployment","description":"Un Deployment en SleakOps significa la instalaci\xf3n de una nueva versi\xf3n de tu aplicaci\xf3n. Esto involucra tres componentes principales, que conforman un Helm Release:","sidebar":"tutorialSidebar"},"project/deployment/more_on_deployment":{"id":"project/deployment/more_on_deployment","title":"Un poco mas sobre los Deployments:","description":"Esta secci\xf3n sirve como una extensi\xf3n de la documentaci\xf3n principal de Deployment. Se recomienda leerla primero para obtener una comprensi\xf3n b\xe1sica.","sidebar":"tutorialSidebar"},"project/deployment/release":{"id":"project/deployment/release","title":"Release","description":"\xbfQu\xe9 es un Release?","sidebar":"tutorialSidebar"},"project/index":{"id":"project/index","title":"Projecto","description":"Un projecto en SleakOps representa una base de c\xf3digo gestionada por un repositorio Git. Tambi\xe9n define el llamado Namespace. Es la entidad central que conecta tu repositorio con el entorno de SleakOps, habilitando la implementaci\xf3n, gesti\xf3n y escalado de tu aplicaci\xf3n en la infraestructura de AWS.","sidebar":"tutorialSidebar"},"project/vargroup/index":{"id":"project/vargroup/index","title":"VariableGroups","description":"Un Vargroup en SleakOps es un recurso que almacena de forma segura informaci\xf3n sensible (como credenciales de bases de datos) dentro de tu cl\xfaster EKS. Asegura que las credenciales y las variables espec\xedficas del entorno se gestionen de manera segura y sean f\xe1cilmente accesibles por tus aplicaciones.","sidebar":"tutorialSidebar"},"project/volumes":{"id":"project/volumes","title":"Volumes","description":"En el contexto de Sleakops, los vol\xfamenes se refieren a los recursos de almacenamiento de AWS/Kubernetes adjuntos a un cl\xfaster. Sirven como almacenamiento general para los contenedores especificados. Los Pods solo pueden interactuar con estos vol\xfamenes si est\xe1n expl\xedcitamente adjuntos a ellos.","sidebar":"tutorialSidebar"},"project/workload/cronjob":{"id":"project/workload/cronjob","title":"Cronjobs","description":"CronJobs son tareas programadas que se ejecutan a intervalos regulares (p. ej., diariamente a las 3 a. m., semanalmente o mensualmente). Automatizan tareas repetitivas como copias de seguridad, limpieza de registros o actualizaciones peri\xf3dicas. Al igual que los Jobs, tambi\xe9n se ejecutan en pods, lo que facilita la revisi\xf3n de sus logs.","sidebar":"tutorialSidebar"},"project/workload/hook":{"id":"project/workload/hook","title":"Hooks","description":"Hooks son tareas que se activan ante eventos espec\xedficos durante un despliegue. Por ejemplo, puedes usar Hooks para ejecutar autom\xe1ticamente una migraci\xf3n de base de datos o recopilar estad\xedsticas cada vez que se despliega una nueva versi\xf3n de tu aplicaci\xf3n. Estas tareas tambi\xe9n se ejecutan en pods, lo que facilita la soluci\xf3n de problemas al revisar los registros (logs).","sidebar":"tutorialSidebar"},"project/workload/index":{"id":"project/workload/index","title":"Workloads","description":"En SleakOps, una Ejecuci\xf3n es simplemente un trabajo definido por el usuario que se ejecuta dentro del cl\xfaster. Dependiendo de c\xf3mo y cu\xe1ndo necesites ejecutar tu carga de trabajo, puedes elegir entre cinco tipos diferentes:","sidebar":"tutorialSidebar"},"project/workload/job":{"id":"project/workload/job","title":"Job","description":"Jobs son tareas de ejecuci\xf3n \xfanica, normalmente utilizadas para operaciones puntuales como actualizaciones, verificaciones del sistema o tareas de mantenimiento. Se ejecutan una vez y luego finalizan, y puedes revisar su progreso o resultados consultando los registros (logs) del pod correspondiente.","sidebar":"tutorialSidebar"},"project/workload/webservice":{"id":"project/workload/webservice","title":"Web Service","description":"Web Services son servicios que se ejecutan de forma continua y se encargan de gestionar interacciones basadas en HTTP, como servir p\xe1ginas web, procesar solicitudes de API o administrar la autenticaci\xf3n de usuarios. En Kubernetes, cada Web Service se ejecuta en uno o varios pods que permanecen activos para responder a solicitudes externas en todo momento.","sidebar":"tutorialSidebar"},"project/workload/worker":{"id":"project/workload/worker","title":"Worker","description":"Workers son procesos en segundo plano que no responden directamente a solicitudes HTTP. En su lugar, se centran en tareas internas como el procesamiento de datos, la gesti\xf3n de trabajos en cola o el env\xedo de notificaciones por correo electr\xf3nico. De forma similar a los Web Services, los Workers tambi\xe9n se ejecutan en pods de Kubernetes, pero normalmente funcionan a partir de eventos o seg\xfan un horario establecido, en lugar de atender solicitudes de red entrantes. Por ejemplo, podr\xedas tener un pod de Worker que escuche mensajes en una cola (por ejemplo, para generar informes) y procese estas tareas sin requerir interacci\xf3n del usuario.","sidebar":"tutorialSidebar"},"provider/accounts":{"id":"provider/accounts","title":"Cuentas","description":"Cuentas del Provider","sidebar":"tutorialSidebar"},"provider/common-errors":{"id":"provider/common-errors","title":"Errores comunes en Providers","description":"Si el proceso de creaci\xf3n de un Provider termina en Error, generalmente se debe a un problema de conexi\xf3n con la cuenta de AWS.","sidebar":"tutorialSidebar"},"provider/deleting-a-provider":{"id":"provider/deleting-a-provider","title":"Eliminar un Provider","description":"- Esta funci\xf3n te permite eliminar un Provider y todo lo que fue creado bajo \xe9l.","sidebar":"tutorialSidebar"},"provider/index":{"id":"provider/index","title":"Providers","description":"En SleakOps, un Provider representa la conexi\xf3n con tu nube de AWS, las credenciales otorgadas a SleakOps para acceder a ella y el conjunto de cuentas creadas para gestionar adecuadamente la infraestructura.","sidebar":"tutorialSidebar"},"provider/schemas":{"id":"provider/schemas","title":"Dise\xf1ando tu Infraestructura","description":"Esquema Simple vs. Esquema M\xfaltiple","sidebar":"tutorialSidebar"},"responsability-model":{"id":"responsability-model","title":"Modelo de Responsabilidad Compartida","description":"Utilizamos de manera transparente todos los servicios de AWS, lo que implica que la responsabilidad se extiende a las pautas establecidas por AWS.","sidebar":"tutorialSidebar"},"user/aws_console_authentication":{"id":"user/aws_console_authentication","title":"Autenticaci\xf3n de la Consola de AWS","description":"C\xf3mo acceder a cualquiera de tus Cuentas.","sidebar":"tutorialSidebar"},"user/index":{"id":"user/index","title":"Usuarios","description":"Creaci\xf3n de usuarios","sidebar":"tutorialSidebar"},"user/vpn":{"id":"user/vpn","title":"Conexi\xf3n a la VPN","description":"Para manejar las conexiones VPN utilizamos Pritunl. Puedes descargar el cliente aqu\xed.","sidebar":"tutorialSidebar"}}}}')}}]);