/**
 * Tutorials Configuration
 *
 * Tags configuration for the tutorials showcase.
 * The tutorial list is automatically generated from MDX files.
 *
 * To add a new tag:
 * 1. Add the tag key to the TagList array
 * 2. Add the tag configuration to the Tags object
 */

// Import generated tutorials data (generated by scripts/generate-tutorials.js)
let tutorialsByLocale = { en: [], es: [] };
try {
  tutorialsByLocale = require("./tutorials-generated.json");
} catch (e) {
  console.warn(
    "tutorials-generated.json not found. Run: node generate_tutorials.js",
  );
}

/**
 * Get tutorials list for the current locale
 * @param {string} locale - The current locale (e.g., 'en', 'es')
 * @returns {Array} - List of tutorials for the locale
 */
export function getTutorialsList(locale = "en") {
  return tutorialsByLocale[locale] || tutorialsByLocale["en"] || [];
}

